var mysql      = require('mysql');


class DBController{
 constructor(){
    this.connection = mysql.createConnection({
        host     : 'localhost',
        user     : 'root',
        password : 'password',
        database : 'northwind',
        port: 3306
        });
   }

   getDataFromTable(tableName){
        let select = 'SELECT * FROM '+ this.connection.config.database + '.' + tableName; 
        return this.getDataBySelect(select);
   }

   getTablesList(){
        let select = 'SHOW TABLES FROM ' + this.connection.config.database;        
        return this.getDataBySelect(select);
   }

   getDataBySelect(select){
    let data;
    this.connection.connect();
    this.connection.query(select, function (error, results, fields) {
        if (error) throw error;
        data = results;
    });
    this.connection.end();
    return data;
}
    
    

}

export default DBController;
